main .cookieconsent {
  width: 98%;
  max-width: 600px;
  border-radius: 5px;
  left: 50%;
  bottom: 5px;
  transform: translateX(-50%);
  z-index: 2147483641;
}

main .cookieconsent .text {
  font-size: 0px;
  text-align: center; 
}

main .cookieconsent .text::before {
  content: 'Utilizamos cookies e tecnologias para melhorar sua experiência em nosso site. Ao continuar navegando, você concorda com nossa ';
  font-size: 13px;
}

main .cookieconsent .text a {
  font-size: 13px;
  text-decoration: underline;
  text-transform: lowercase;
}

main .cookieconsent .text::after {
  content: '.';
  font-size: 13px;
}

